(function () {
    const modDirectory = "/mods/";

    class ModLoader {
        constructor(runtime, version) {
            window.ovoModLoader = this;

            this.runtime = runtime;
            this.version = version;

            this.initialised = false;
            this.mods = {};
            this.loadedMods = [];

            console.log("Modloader: Got runtime, initialising modloader with version " + version);
            this.#init();
        }

        async #init() {
            this.mods = await fetch(this.getModDirectory() + this.version + ".json").then(res => res.json());
            this.loadModURL("modapi.js", true, false);

            window.addEventListener("keydown", (event) => {
                if (event.code === "KeyL") {
                    if (event.shiftKey) {
                        this.promptMod();
                    }
                }
            });
            this.#initDomUI();

            this.initialised = true;
        }

        async #initDomUI() {
            const style = document.createElement("style");
            style.innerHTML = `
            .ovo-modloader-button {
                background-color: white;
                border: solid;
                border-color: black;
                border-width: 6px;
                font-family: "Retron2000";
                position: absolute;
                z-index: 999999;
                cursor: pointer;
            }

            .ovo-modloader-button:hover {
                background-color: rgba(200, 200, 200, 1);
            }
            `
            document.head.appendChild(style);

            const toggleButton = document.createElement("button");
            toggleButton.id = "ovo-modloader-toggle-button";
            toggleButton.innerText = "";

            const loadIcon = document.createElement("img");
            loadIcon.src = "data:image/svg+xml;base64,"
            loadIcon.style.width = "38px";
            loadIcon.style.height = "38px";

            toggleButton.appendChild(loadIcon);
            toggleButton.classList.add("ovo-modloader-button");
            toggleButton.style.top = "50%";
            toggleButton.style.right = "0%";
            toggleButton.style.transform = "translateY(-50%)";
            toggleButton.style.width = "50px";
            toggleButton.style.height = "50px";
            toggleButton.onclick = this.promptMod.bind(this);
            document.body.appendChild(toggleButton);
        }

        notify(title, text, image = "./speedrunner.png") {
            cr.plugins_.sirg_notifications.prototype.acts.AddSimpleNotification.call(
                this.runtime.types_by_index.find(
                    (type) => type.plugin instanceof cr.plugins_.sirg_notifications
                ).instances[0],
                title,
                text,
                image
            );
        }

        getModDirectory() {
            return this.getScriptDir() + modDirectory;
        }

        getScriptPath() {
            return decodeURI(new Error().stack.match(/([^ \n\(@])*([a-z]*:\/\/\/?)*?[a-z0-9\/\\]*\.js/ig)[0]);
        }

        getScriptDir() {
            return this.getScriptPath().split("/").slice(0, -1).join("/") + "/";
        }

        getURLName(url) {
            return url.split("/").pop().split(".").slice(0, -1).join(".");
        }

        getIsScriptLoaded(name) {
            return this.loadedMods.includes(name);
        }

        getIsValidURL(str) {
            const pattern = new RegExp('^(https?:\\/\\/)?' +
                '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|' +
                '((\\d{1,3}\\.){3}\\d{1,3}))' +
                '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*' +
                '(\\?[;&a-z\\d%_.~+=-]*)?' +
                '(\\#[-a-z\\d_]*)?$', 'i');
            return pattern.test(str);
        }

        #createScript(src, key, name, notify = true) {
            const js = document.createElement("script");
            js.type = "application/javascript";
            js.src = src;

            js.onload = () => {
                this.loadedMods.push(key);
                if (notify) {
                    this.notify("Mod loaded", name);
                }
            }

            js.onerror = () => {
                if (notify) {
                    this.notify("Error loading mod", name);
                }
            }

            document.head.appendChild(js);
        }

        loadModURL(url, local = false, notify = true) {
            if (local) {
                url = this.getModDirectory() + this.version + "/" + url;
            }

            const name = this.getURLName(url);

            if (this.getIsScriptLoaded(name)) {
                if (notify) {
                    this.notify("Mod already loaded", name);
                }
                return;
            }

            this.#createScript(url, name, name, notify);
        }

        loadModJSON(key, json, notify = true) {
            const url = this.getModDirectory() + this.version + "/" + json.url;
            const name = json.name;

            if (this.getIsScriptLoaded(key)) {
                if (notify) {
                    this.notify("Mod already loaded", name);
                }
                return;
            }

            this.#createScript(url, key, name, notify);
        }

        loadModJS(modJS, notify = true) {
            setTimeout(modJS, 0);

            if (notify) {
                this.notify("Mod loaded", "Loaded JS code");
            }
        }

        promptMod() {
            const input = prompt("Enter mod URL, ID, or JS code");
            if (input === null) return;
            const key = input.toLocaleLowerCase();

            if (this.mods[key]) {
                this.loadModJSON(key, this.mods[key], true);
            } else {
                if (this.getIsValidURL(input)) {
                    this.loadModURL(input, false, true);
                } else {
                    this.loadModJS(input, true);
                }
            }
        }
    }

    if (typeof window.ovoModLoader === "undefined") {
        const version = window.cr !== undefined ? "v1" : "v2";
        console.log("Modloader: Loading modloader version " + version);

        switch (version) {
            case "v1":
                if (typeof window.cr_getC2Runtime !== "undefined" && window.cr_getC2Runtime()) {
                    console.log("Modloader: C2 runtime already loaded, hooking into it");
                    new ModLoader(window.cr_getC2Runtime(), version);
                } else {
                    console.log("Modloader: C2 runtime not loaded yet, hooking into createRuntime");
                    const create = window.cr_createRuntime;
                    window.cr_createRuntime = function (canvasId) {
                        new ModLoader(create(canvasId), version);
                    }
                }
                break;
            case "v2":
                if (typeof window.c3_runtimeInterface !== "undefined" && window.c3_runtimeInterface._GetLocalRuntime()) {
                    console.log("Modloader: C3 runtime already loaded, hooking into it");
                    new ModLoader(window.c3_runtimeInterface._GetLocalRuntime(), version);
                } else {
                    console.log("Modloader: C3 runtime not loaded yet, hooking into requestAnimationFrame");
                    const requestAnimation = window.requestAnimationFrame;
                    window.requestAnimationFrame = function () {
                        new ModLoader(window.c3_runtimeInterface._GetLocalRuntime(), version);
                        requestAnimation.apply(this, arguments);
                        window.requestAnimationFrame = requestAnimation;
                    }
                }
                break;
            default:
                console.error("Modloader: Can't Determine OVO Version. Aborting.");
                break;
        }
    }
})();